package Edubridge;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=5;i++)
		{
			System.out.println(i);
		}
		
		
		int j=1;
		while(j<=5)
		{
			System.out.println(j);
			j=j+1;
		}
		
		int k=1;
		do 
		{
			System.out.println(k);
			k=k+1;
		}while(k<=5);
	}

}
