package Edubridge;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=20;
		int age=45;
		int n=5;
		if(a>b)		System.out.println(a+" is greater");
		else		System.out.println(b+" is greater");
		
		if(age>=18)	System.out.println(age+" Eligible for voting");
		else 		System.out.println("Not eligible");
		
		if(n%2==0)	System.out.println("Even no");
		else 		System.out.println("Odd no");
		
		
		
	}

}
